                        PathImage1 = "/files/image/ExerciseImage/BaseExercise/Legs/1/приседания1.jpg",
                        PathImage2 = "/files/image/ExerciseImage/BaseExercise/Legs/1/приседания2.jpg",
                        PathImage3 = "/files/image/ExerciseImage/BaseExercise/Muscle/legs.png",
                        NameImage1 = "Приседания со штангой.jpg",
